// Page Home

import { useState, useEffect } from 'react';
import { appTitle, endPointSearch, endPointAllBreeds, apiKey } from '../globals/globalVariables';

function PageHome(){

	const [breeds, setBreeds] = useState(false);
	const [selectedBreed, setSelectedBreed] = useState(false);
	const [cat, setCat] = useState(false);

	useEffect(() => {
		document.title = `${appTitle} - Home`;
	},[]);
	useEffect(() => {
		const getCatImage = async()=>{
			const res = await fetch(endPointAllBreeds);

			const data = await res.json();
			console.log(data[0].url);

		}
		getCatImage();
	}, []);
	useEffect(() => {
		const fetchBreeds = async () => {
			// endPoint = https://api.thecatapi.com/v1/images/search?
			const res = await fetch(endPointAllBreeds, {
				headers: {
					'x-api-key': apiKey
				  }
			});
			let data = await res.json();
			setBreeds(data);
			setSelectedBreed(data[0].id);
		  }

		  fetchBreeds();
	},[]);

	function handleGetCat(e){

		e.preventDefault();

		const fetchCat = async () => {
			// endPoint = https://api.thecatapi.com/v1/images/search?
			const res = await fetch(`${endPointSearch}breed_ids=${selectedBreed}`, {
				headers: {
					'x-api-key': apiKey
				  }
			});
			let data = await res.json();
			const catObj = { src: data[0].url, alt: data[0].breeds[0].name, id: data[0].id } 
			console.log(data);
			setCat(catObj);
		  }

		  fetchCat();

	}

	function handleChangeBreed(e){
		setSelectedBreed(e.target.value);
	}

	return (
		<main>
			<section className="cat-photo-section">
				<h2>Cat Photos</h2>
				<div className="cat-container">
					<div className="get-cat-form-container">
						<form onSubmit={handleGetCat} className="get-cat-form">
							<div className="input-group">
								<label htmlFor="selectBreed">Select Breed: </label>
								{breeds && <select name="selectBreed" id="selectBreed" size="5" value={selectedBreed} onChange={handleChangeBreed}>
									{breeds.map(breed => <option key={breed.id} value={breed.id}>{breed.name}</option> )}
								</select>}
							</div>
							<div className="submit-group">
								{cat === false ? 
									<button type="submit">Get Cat Photo</button> :
									<button type="submit">Get Another Cat Photo</button>
								}
							</div>
						</form>
					</div>
					<div className="cat-image">
						{cat && <img src={cat.src} alt={cat.alt} />}
					</div>
				</div>
			</section>
		</main>
	);

}

export default PageHome;
